                  const numberValue = Number(value);
                  if (isNaN(numberValue)) {
                    return params.oldValue;
                  }
                  // 如果设置了 precision，四舍五入到指定精度
                  if (precision !== undefined && precision !== null) {
                    return Number(numberValue.toFixed(precision));
