import numeral from 'numeral';

{
  field: 'price',
  editable: true,
  numberConstraints: { min: 0, max: 9999.99, precision: 2 },  // 🔑 主要约束
  render: ({ value, isEditing, onChange }) => {
    if (isEditing) {
      return (
        <InputNumber
          defaultValue={value}
          onChange={onChange}
          min={0}           // 推荐配置，与 numberConstraints 保持一致
          max={9999.99}     // 推荐配置，与 numberConstraints 保持一致
          precision={2}     // 推荐配置，与 numberConstraints 保持一致
        />
      );
    }
    // 💡 使用 numeral 格式化显示（去除末尾的 0）
    // format: '0,0.[00]' 表示最多2位小数，末尾的0会被省略
    return <span>¥{value != null ? numeral(value).format('0,0.[00]') : '--'}</span>;
  }
} 这个 demo 里面帮我把 格式化的部分用 core 里面的 格式化方法处理

