  /**
   * 初始化时请求数据（只执行一次，防止 Strict Mode 重复执行）
   */
  useEffect(() => {
    console.log('[useEffect] 初始化 effect 执行', {
      initialized: initializedRef.current,
      pageSize,
    });

    // 如果已经初始化过，跳过（防止 Strict Mode 重复执行）
    if (initializedRef.current) {
      console.log('[useEffect] 已初始化，跳过');
      return;
    }

    console.log('[useEffect] 首次初始化，开始请求');
    initializedRef.current = true;
    fetchData(1, pageSize);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // 空数组确保只在组件挂载时执行一次

  /**
   * params 变化时重置到第一页并重新请求（声明式方式）
   */
  useEffect(() => {
    // 跳过首次设置（首次是初始化，已经在上面的 useEffect 中处理）
    if (isFirstParamsRef.current) {
      isFirstParamsRef.current = false;
      console.log('[useEffect] params 首次设置，跳过');
      return;
    }

    // 跳过初始化阶段（初始化已经在上面的 useEffect 中处理）
    if (!initializedRef.current) {
      console.log('[useEffect] 组件未初始化，跳过');
      return;
    }

    console.log('[useEffect] params 变化，重置到第一页', { params });
    setCurrentPage(1);
    fetchData(1, pageSize);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [params]); // 监听 params 变化 看看能不能优化?

