# 实际配置文件位于服务器：/etc/nginx/conf.d/locations/terminal-app-coms.conf
#
# 配置说明：
# - 该配置文件会被 /etc/nginx/conf.d/default.conf include 引入
# - 只包含 /terminal-app-coms/ 路径的 location 配置
#
# 手动修改服务器配置后，执行以下命令使其生效：
# ssh root@10.1.5.116 "nginx -t && systemctl reload nginx"

# 不带尾部斜杠的路径重定向到带斜杠的路径
location = /terminal-app-coms {
    return 301 /terminal-app-coms/;
}

# terminal-app-coms 文档站点
location /terminal-app-coms/ {
    alias /var/www/terminal-app-coms/dist/;
    index index.html;

    # 静态资源缓存配置
    location ~* \.(js|css|png|jpg|jpeg|gif|ico|svg|woff|woff2|ttf|eot)$ {
        expires 1y;
        add_header Cache-Control "public, immutable";
        access_log off;
    }

    # HTML 文件不缓存
    location ~* \.html$ {
        add_header Cache-Control "no-cache, no-store, must-revalidate";
        add_header Pragma "no-cache";
        add_header Expires 0;
    }

    # SPA 路由处理 - dumi 使用 browser history 模式
    try_files $uri $uri/ /terminal-app-coms/index.html;
}
